<script setup>
import echartDecoration from '../../utils/echartDecoration.vue';
import { setCardInfoConfig } from '@/utils/js/echartsConfig/basePersonal/cardInfoConfig'
import { onMounted, ref, getCurrentInstance } from 'vue';
import card from '@/components/echarts/commen/card.vue'

let modelBind=1,idBind=1
const time_window = ref('1')
const questionContent = "How old are you?";
const loading = ref('true')
const { proxy } = getCurrentInstance()
const cardInfo = ref({
    "forecastLoss": 0.0,
    "forecastPrice": 0.0,
    "forecastBack": 0.0,
    avgdiff: 0.0,
    "clvUid": 0
})
const props = defineProps({
    width: {
        type: Number,
        default: 'auto'
    },
    height: {
        type: Number,
        default: 300
    }
})
const emit = defineEmits('updateClvInfo')
const title = ref('国内随机森林预测信息')

onMounted(() => {
    chartDraw()
})

const dayChange = (val) => {
    chartDraw(modelBind,idBind)
}

const chartDraw = (model=1,id = 1) => {
    modelBind = model
    idBind = id
    loading.value = true
    setCardInfoConfig(model, id, time_window.value).then(res => {
        title.value = `${id}用户随机森林预测信息`
        loading.value = false
        cardInfo.value = res
        emit('updateClvInfo',res)
    })
}

defineExpose({
    chartDraw,
    cardInfo
})

</script>

