<script setup>
import echartDecoration from '../../utils/echartDecoration.vue';
import { setCardInfoConfig } from '@/utils/js/echartsConfig/picGroup/cardInfoConfig'
import { onMounted, ref, getCurrentInstance,defineEmits } from 'vue';
import card from '@/components/echarts/commen/card.vue'

let addressBind, modelBind, tagBind
const time_window = ref('1')
const questionContent = "How old are you?";
const loading = ref('true')
const { proxy } = getCurrentInstance()
const cardInfo = ref({
    "avgLoss": 0.0,
    "sumPrice": 0.0,
    "avgBack": 0.0,
    "avgdiff": 0.0,
    "personal_avg":0
})

const emit = defineEmits('update')
const props = defineProps({
    width: {
        type: Number,
        default: 'auto'
    },
    height: {
        type: Number,
        default: 300
    }
})
const title = ref('国内随机森林预测信息')

onMounted(() => {
    chartDraw(addressBind, modelBind, tagBind)
})

const dayChange = (val) => {
    chartDraw()
}

const chartDraw = (address, model = 1, tag = '重要价值客户') => {
    addressBind = address
    modelBind = model
    tagBind = tag
    loading.value = true
    setCardInfoConfig(address, model, tag, time_window.value).then(res => {
        title.value = `${address == null ? '国内' : address}随机森林预测信息`
        loading.value = false
        cardInfo.value = res
        emit('update',cardInfo.value)
    })
}

defineExpose({
    chartDraw,
    cardInfo
})

</script>

